import { Controller } from '@hotwired/stimulus';
import {Html5QrcodeScanner} from 'html5-qrcode';
import Quagga from '@ericblade/quagga2'; // barcode only https://serratus.github.io/quaggaJS/
import ISBN from 'isbn3'

// for photos, demo not working well though
// https://daviddalbusco.medium.com/take-photo-and-access-the-picture-library-in-your-pwa-without-plugins-876dc92989b
// https://developer.mozilla.org/en-US/docs/Web/API/Media_Capture_and_Streams_API/Taking_still_photos#demo]
// https://web.dev/articles/media-capturing-images#grab_a_snapshot
/*
* The following line makes this controller "lazy": it won't be downloaded until needed
* See https://github.com/symfony/stimulus-bridge#lazy-controllers
*/
/* stimulusFetch: 'lazy' */
export default class extends Controller {
    static targets = ['results', 'camera']
    static values = {
        testIsbn: String,
    }

    beep() {
        var snd = new Audio("data:audio/wav;base64,");
        snd.play();
    }

    isValid(isbn)
    {
        return ISBN.audit(isbn).validIsbn;
    }
    connect() {
        super.connect();
        this.resultsTarget.innerHTML = this.testIsbnValue;
        this.lastValidScan = '';

        if (this.isValid(this.testIsbnValue)) {
            // this.fetchInfo(this.testIsbnValue);
        }




        var html5QrcodeScanner = new Html5QrcodeScanner(
            // this.cameraTarget.element.attribute('id'),
            'qr-reader',
            { fps: 5});
        html5QrcodeScanner.render( (decodedText, decodedResult) => {
            // this.resultsTarget.innerHTML = decodedText;
            // if it's isbn...
            const info = this.fetchInfo(decodedText);
            console.log(`Code scanned = ${decodedText}`, decodedResult);
        });

    }

    async fetchInfo(id)
    {
        console.log(ISBN.parse(id));

        // really 978 or 979, the latter is 13-digits
        if (this.isValid(id)) {
            if (this.lastValidScan == id) {
                return;
            }
            this.lastValidScan = id;
            // let li = this.addToList(id);
            let url = "/isbn/" + id;
            this.beep();
            this.resultsTarget.innerHTML = '...' + id;

            const response = await fetch(url);
            const info = await response.json().catch(error => console.error(error));
            this.resultsTarget.innerHTML = info.title;
            // li.textContent = id + '/' + info.title;
            // console.log(info);
            // Create a new element
        }
    }

    addToList(id)
    {
        let li = document.createElement('li');
        li.textContent = '...' + id;

// Get the parent node
        let ul = document.querySelector('#list');
        ul.append(li);
        let threshold = 3;

        if (ul.childNodes.length > threshold)
            ul.lastChild.parentNode.removeChild(ul.firstChild);

        return li;

    }

    async quagga()
    {
        Quagga.init({
            inputStream : {
                name : "Live",
                type : "LiveStream",
                target: document.querySelector('#qr-reader')
                // target: this.cameraTarget.element
            },

            decoder : {
                // readers : ["code_128_reader"]
            },
            onProcessed: (x) => console.error(x),
            onDetected: (x) => console.error(x),
        }, function(err) {
            if (err) {
                console.log(err);
                return
            }
            console.log("Initialization finished. Ready to start");

            Quagga.onDetected(function(result) {
                var code = result.codeResult.code;
                console.error(result);

                if (App.lastResult !== code) {
                    App.lastResult = code;
                    var $node = null, canvas = Quagga.canvas.dom.image;

                    $node = $('<li><div class="thumbnail"><div class="imgWrapper"><img /></div><div class="caption"><h4 class="code"></h4></div></div></li>');
                    $node.find("img").attr("src", canvas.toDataURL());
                    $node.find("h4.code").html(code);
                    $("#result_strip ul.thumbnails").prepend($node);
                }
            });
            Quagga.start();

        });

    }

    // ...
}
